import React from 'react';
import UserForm from './UserForm';

interface UserDetailProps {
  user: { name: string; email: string; contact: string; description: string };
  onUserUpdate: (userData: { name: string; email: string; contact: string; description: string }) => void;
  handleDelete: () => void;
}

const UserDetail: React.FC<UserDetailProps> = ({ user, onUserUpdate, handleDelete }) => {
  return (
    <div>
      <h2>{user.name}</h2>
      <p>Email: {user.email}</p>
      <p>Description: {user.description}</p>
      <UserForm user={user} onSave={onUserUpdate} />
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default UserDetail;
