import React, { useState } from 'react';

interface UserFormProps {
  onSave: (userData: { name: string; email: string; contact: string; description: string }) => void;
}

const UserForm: React.FC<UserFormProps> = ({ onSave }) => {
  const [formData, setFormData] = useState({ name: '', email: '', contact: '', description: '' });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave(formData);
    setFormData({ name: '', email: '', contact: '', description: '' });
  };

  return (
    <form onSubmit={handleSubmit} className="user-form">
      <div className="form-group">
        <label>Name</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
      </div>
      <div className="form-group">
        <label>Email</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required />
      </div>
      <div className="form-group">
        <label>Contact</label>
        <input type="text" name="contact" value={formData.contact} onChange={handleChange} required />
      </div>
      <div className="form-group">
        <label>Description</label>
        <textarea name="description" value={formData.description} onChange={handleChange} required></textarea>
      </div>
      <button type="submit" className="submit-btn">Save</button>
    </form>
  );
};

export default UserForm;
